utilityMethods.utility:
function transSYXSendReq(transaction) console("transSYXSendReq") fileToSend = utils.openFileWindow("SYX file to send", File.getSpecialLocation(File.userHomeDirectory), "*.*", true)
if fileToSend:existsAsFile() then result = MemoryBlock (0, true) fileToSend:loadFileAsData (result)
if result:getSize() <= 0 then utils.warnWindow ("Send SYX File", "Reading the file resultet in 0 data bytes") return end
debug ("sending file: "..fileToSend:getFullPathName())
transaction:setRequest (result) else utils.warnWindow ("Send SYX File", "Can't read selected file") end end
